import apiClient from './index'

export default {
  // 获取所有衣物类别
  getCategories() {
    return apiClient.get('/categories')
  },

  // 获取所有衣物
  getClothingItems() {
    return apiClient.get('/clothing-items')
  },

  // 根据类别获取衣物
  getClothingItemsByCategory(categoryId) {
    return apiClient.get(`/clothing-items?categoryId=${categoryId}`)
  },

  // 获取衣物详情
  getClothingItemDetail(id) {
    return apiClient.get(`/clothing-items/${id}`)
  },

  // 添加衣物
  addClothingItem(item) {
    return apiClient.post('/clothing-items', item)
  },

  // 更新衣物信息
  updateClothingItem(id, updates) {
    return apiClient.put(`/clothing-items/${id}`, updates)
  },

  // 删除衣物
  deleteClothingItem(id) {
    return apiClient.delete(`/clothing-items/${id}`)
  },

  // 搜索衣物
  searchClothingItems(keyword) {
    return apiClient.get(`/clothing-items/search?q=${keyword}`)
  }
}
