import apiClient from './index'

export default {
  // 获取所有搭配
  getOutfits() {
    return apiClient.get('/outfits')
  },

  // 根据标签获取搭配
  getOutfitsByTag(tag) {
    return apiClient.get(`/outfits?tag=${tag}`)
  },

  // 获取搭配详情
  getOutfitDetail(id) {
    return apiClient.get(`/outfits/${id}`)
  },

  // 添加新搭配
  addOutfit(outfit) {
    return apiClient.post('/outfits', outfit)
  },

  // 更新搭配
  updateOutfit(id, updates) {
    return apiClient.put(`/outfits/${id}`, updates)
  },

  // 删除搭配
  deleteOutfit(id) {
    return apiClient.delete(`/outfits/${id}`)
  },

  // 切换搭配的喜欢状态
  toggleLike(id) {
    return apiClient.post(`/outfits/${id}/like`)
  }
}
