// 标签分类数据
export const tagCategories = [
  {
    id: 1,
    name: '风格',
    icon: 'palette',
    tags: ['休闲', '运动', '商务', '正式', '摇滚', '时尚', '复古', '街头', '简约', '优雅']
  },
  {
    id: 2,
    name: '季节',
    icon: 'cloud-sun',
    tags: ['春季', '夏季', '秋季', '冬季']
  },
  {
    id: 3,
    name: '场合',
    icon: 'calendar',
    tags: ['日常通勤', '周末休闲', '商务会议', '约会聚餐', '运动健身', '旅行度假', '正式场合', '家庭聚会']
  },
  {
    id: 4,
    name: '材质',
    icon: 'fabric',
    tags: ['棉', '牛仔布', '聚酯纤维', '羊毛', '皮革', '丝绸', '亚麻', '尼龙', '合成材料', '真皮']
  },
  {
    id: 5,
    name: '颜色',
    icon: 'droplet',
    tags: ['黑色', '白色', '灰色', '蓝色', '红色', '绿色', '黄色', '棕色', '紫色', '粉色']
  }
]

// 获取所有标签
export const getAllTags = () => {
  return tagCategories.flatMap(category => category.tags)
}

// 根据分类ID获取标签
export const getTagsByCategory = (categoryId) => {
  const category = tagCategories.find(cat => cat.id === categoryId)
  return category ? category.tags : []
}

// 根据标签获取分类
export const getCategoryByTag = (tag) => {
  for (const category of tagCategories) {
    if (category.tags.includes(tag)) {
      return category
    }
  }
  return null
}
