// Mock API服务
const mockOutfitApi = {
  // 获取所有搭配
  getOutfits: async () => {
    // 模拟网络延迟
    await new Promise(resolve => setTimeout(resolve, 300))
    return { success: true, data: [...mockOutfits] }
  },

  // 根据标签获取搭配
  getOutfitsByTag: async (tag) => {
    await new Promise(resolve => setTimeout(resolve, 300))
    const filteredOutfits = mockOutfits.filter(outfit => outfit.tag === tag)
    return { success: true, data: filteredOutfits }
  },

  // 获取搭配详情
  getOutfitDetail: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 300))
    const outfit = mockOutfits.find(o => o.id === id)
    if (outfit) {
      return { success: true, data: outfit }
    } else {
      return { success: false, message: '未找到该搭配' }
    }
  },

  // 添加新搭配
  addOutfit: async (outfit) => {
    await new Promise(resolve => setTimeout(resolve, 500))
    const newOutfit = {
      ...outfit,
      id: mockOutfits.length + 1,
      liked: false,
      likes: 0
    }
    mockOutfits.push(newOutfit)
    return { success: true, data: newOutfit }
  },

  // 更新搭配
  updateOutfit: async (id, updates) => {
    await new Promise(resolve => setTimeout(resolve, 500))
    const index = mockOutfits.findIndex(o => o.id === id)
    if (index !== -1) {
      mockOutfits[index] = { ...mockOutfits[index], ...updates }
      return { success: true, data: mockOutfits[index] }
    } else {
      return { success: false, message: '未找到该搭配' }
    }
  },

  // 删除搭配
  deleteOutfit: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 400))
    const index = mockOutfits.findIndex(o => o.id === id)
    if (index !== -1) {
      mockOutfits.splice(index, 1)
      return { success: true }
    } else {
      return { success: false, message: '未找到该搭配' }
    }
  },

  // 切换搭配的喜欢状态
  toggleLike: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 300))
    const outfit = mockOutfits.find(o => o.id === id)
    if (outfit) {
      outfit.liked = !outfit.liked
      if (outfit.liked) {
        outfit.likes++
      } else {
        outfit.likes--
      }
      return { success: true, data: outfit }
    } else {
      return { success: false, message: '未找到该搭配' }
    }
  }
}

